#include <vector>
#include <iostream>
#include <algorithm>
#include <stack>
using namespace std;

int largestRectangleArea(vector<int>& heights) {
    int ans=-999999999;
    int len=heights.size();

    int area=0;

    stack <int> st;
    int idx;
    int top_rem;
    int h,b;
    for(int i=0; i<len; ){
        // cout<<"i: "<<i<<" ";
        if(st.empty() || heights[i] >= heights[st.top()]){
            // cout<<"push "<<i<<"\n";
            st.push(i);
            i++;
        }
        else{

            top_rem=st.top();
            // cout<<"top: "<<top_rem<<", ";
            st.pop();

            h=heights[top_rem];

            if(st.empty()){
                // cout<<"empty stack, ";
                b=i;
            }

            else //trick
                b=i-st.top()-1;

            // cout<<" b: "<<b<<" ";

            area=h*b;
            // cout<<"ar: "<<area<<"\n";

            if(area>ans){
                ans=area;
            }
        }
        idx=i;

    }
    while(st.empty()==false){
        top_rem=st.top();
        st.pop();


        h=heights[top_rem];

        if(st.empty()){
            b=idx;
        }
        else b=idx-st.top()-1;
        // cout<<"h: "<<h<<" b: "<<b<<" ";
        area=h*b;
        // cout<<"ar: "<<area<<"\n";
        if(area>ans){
            ans=area;
        }
    }



    return ans;
}

void convert2int(vector<vector<char>>& arr, int rows,int cols,vector <vector <int>>& mat){

	for(int i=0;i<rows;i++){
		for(int j=0;j<cols;j++){
			mat[i][j]=arr[i][j]-'0';
		}
	}
}
int maximalRectangle(vector<vector<char>>& matrix) {
    int rows=matrix.size();
    if(rows==0)
        return 0;
    int cols=matrix[0].size();
    
    vector <vector <int>> mat(rows, vector<int>(cols,0));
    convert2int(matrix,rows,cols,mat);
    int ar;
    int max_ar;
    //find max area of row 0
    
    max_ar=largestRectangleArea(mat[0]);
    // cout<<"max area of row 0: "<<max_ar<<"\n";

    //for rows 1 to n
    for(int r=1; r<rows;r++){
        //for each col in this row
        // cout<<"row r "<<r<<" ";
        for(int c=0;c<cols;c++){
            if(mat[r][c]){
                mat[r][c]+=mat[r-1][c];
            }
        }        
        ar=largestRectangleArea(mat[r]);
        // cout<<"ar of row "<<r<<" "<<ar<<"\n";
        if(max_ar<ar){
                max_ar=ar;
        }
    }

    for(auto i:mat){
        for(auto j:i){
            cout<<j<<" ";
        }
        cout<<"\n";
    }

    return max_ar;        
}

