#include <string>
#include <iostream>
#include <vector>
#include <unordered_map>

using namespace std;
	 vector<int> findSubstring(string s, vector<string>& words) {
	 	unordered_map <string,int> table;
	 	vector <int> indices;
	 	cout<<"S.length: "<<s.length()<<"\n";
	 	int len=0;	
	 	for(auto i=words.begin();i!=words.end();i++){
	 		table[*i]+=1;
	 		len+=(*i).length();
	 	}
	 	// cout<<s<<",----\n";
	 	for(auto i=table.begin();i!=table.end();i++){
	 		cout<<i->first<<": "<<i->second<<"\n";
	 	}
	 	cout<<"------\n";
	 	cout<<"len: "<<len<<"\n";
	 	int num_of_words=words.size();
	 	int len_per_word=len/num_of_words;

	 	for(int i=0;i<s.length();i++){
	 		unordered_map <string,int> temp_table(table);

	 		int j=i;
	 		auto temp_i=temp_table.begin();
	 		while(j<i+len){
	 			
	 			//take substring of length len
	 			
	 			// cout<<" temp_i->first: "<<temp_i->first<<"\n\n";
	 			string to_match=s.substr(j,(temp_i->first).length());
	 			cout<<"to_match: "<<to_match<<"| ";
	 			if(to_match.length()<(temp_i->first).length()){
	 				break;
	 			}
	 			//search if to_match is present in hashmap
	 			if(temp_table.find(to_match)!=temp_table.end()){
	 				//word is present
	 				//decrement the count
	 				cout<<"present, ";
	 				if(temp_table[to_match]==0){
	 					// cout<<"here\n";
	 					break;
	 				}
	 				temp_table[to_match]--;
	 				cout<<"temp_table: "<<temp_table[to_match]<<"\n";
	 				temp_i++;
	 				if(temp_i==temp_table.end())
	 					temp_i=temp_table.begin();

	 			}
	 			//word is not present
	 			else{
	 				// cout<<"break\n";
	 				break;
	 			}
	 			// cout<<"new j ";
	 			j+=len_per_word;
	 			// cout<<j<<"\n";
	 			if(j>=s.length()){
	 				break;
	 			}
	 		}


	 		int count=0;
	 		for(auto it=temp_table.begin();it!=temp_table.end();it++){
	 			if(it->second>0){
	 				count++;
	 			}
	 		}
	 		if(count==0){
	 			indices.push_back(i);
	 		}
	 	}

	 	return indices;
	 }

